--METODO PARA CONTAR PALABRAS, GENERADOR DE ESTADISTICAS
contarPalabras text = let
						minusculas = [toLower x | x <- text]
						letras = [c | c <- minusculas, (isPunctuation c) == False]
						palabras = words letras
						keys = [w | w <- palabras, not (elem w stopWords)]
		   			  in
						map (funContar keys) keys

funContar k palabra = (palabra,(contar k palabra))
contar [] w = 0
contar (x:xs) w = if (w == x)
			then 1 + (contar xs w)
			else (contar xs w)

--METODO PARA ANALIZAR EL TEXTO
identificar text = let
					minusculas = [toLower x | x <- text]
					letras = [c | c <- minusculas, (isPunctuation c) == False]
					palabras = words letras
					keys = [w | w <- palabras, not (elem w stopWords)]

					analisisPolitica = funContarE ([c | (w,c) <- estadisticaPolitica, (elem w keys)])
					analisisDeportes = funContarE ([c | (w,c) <- estadisticaDeportes, (elem w keys)])
					analisisReligion = funContarE ([c | (w,c) <- estadisticaReligion, (elem w keys)])

					categoria = funCategoria analisisPolitica analisisDeportes analisisReligion
				   in
				    categoria

funContarE [] = 0
funContarE (x:xs) = x + (funContarE xs) 

funCategoria politica deportes religion = if (politica > deportes)
											then (
												if (politica > religion)
													then "El texto es de Politica"
													else "El texto es de Religion"
											)
											else (
												if (deportes > religion)
													then "El texto es de Deportes"
													else "El texto es de Religion"
											)